    <script>
        var vaiButton = document.getElementById("vai");
        var naoButton = document.getElementById("nao-vai");
        var vaiList = document.getElementById("vai-list");
        var naoList = document.getElementById("nao-list");

        vaiButton.addEventListener('click', function(){
            if (naoList.classList.contains('selected-list')){
                naoList.classList.remove('selected-list');
                naoButton.classList.remove('selected');
                vaiList.classList.add('selected-list');
                vaiButton.classList.add('selected');
            }
        })
        naoButton.addEventListener('click', function(){
            if (vaiList.classList.contains('selected-list')){
                vaiList.classList.remove('selected-list');
                vaiButton.classList.remove('selected');
                naoList.classList.add('selected-list');
                naoButton.classList.add('selected');
            }
        })

